<script>
    export default {
        name: "AboutMe",
        methods:{
            openPage(to){
                for(let index in this.links){
                    this.$store.dispatch('setLinkIsActive', {address: this.links[index].to, value: this.links[index].to === to})
                }
                // eslint-disable-next-line no-unused-vars
                this.$router.push(to).catch(err => {})
            }
        },
        computed:{
            links(){
                return this.$store.getters.getAvailableLinks;
            },
        },
    }
</script>

